import { BigBadge } from "./BigBadge";
import { SmallBadge } from "./SmallBadge";
import {
  Construction,
  Download,
  Github,
  NotebookText,
  Play,
} from "lucide-react";
import { Tooltip, TooltipContent, TooltipTrigger } from "../ui/tooltip";

export function Substituted({
  children,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <Tooltip>
      <TooltipTrigger className="underline decoration-wavy decoration-1 underline-offset-4">
        {children}
      </TooltipTrigger>
      <TooltipContent side="top">
        <p className="text-center">
          Technology was substituted <br /> for easier demo deployment
        </p>
      </TooltipContent>
    </Tooltip>
  );
}

export function OnlineDemo({ ...props }: React.ComponentProps<"a">) {
  return (
    <a {...props}>
      <BigBadge className="border-[lime] text-[lime] shadow-[lime]">
        <Play fill="lime" /> Online demo
      </BigBadge>
    </a>
  );
}

export function SourceCode({ ...props }: React.ComponentProps<"a">) {
  return (
    <a {...props}>
      <BigBadge className="border-[cyan] text-[cyan] shadow-[cyan]">
        <Github />
        Source code
      </BigBadge>
    </a>
  );
}

export function DownloadableDemo({ ...props }: React.ComponentProps<"a">) {
  return (
    <a {...props}>
      <BigBadge className="border-[magenta] text-[magenta] shadow-[magenta]">
        <Download />
        Downloadable demo
      </BigBadge>
    </a>
  );
}

export function ReadMore({ ...props }: React.ComponentProps<"a">) {
  return (
    <a {...props}>
      <BigBadge className="border-foreground text-foreground shadow-foreground">
        <NotebookText />
        Read more
      </BigBadge>
    </a>
  );
}

export function UnderDevelopment() {
  return (
    <Tooltip>
      <TooltipTrigger>
        <BigBadge className="cursor-default border-[yellow] text-[yellow] shadow-[yellow]">
          <Construction />
          Under development
        </BigBadge>
      </TooltipTrigger>
      <TooltipContent side="bottom">
        <p>I'm actively working on it!</p>
      </TooltipContent>
    </Tooltip>
  );
}

